require(['jquery'],function($){

	$(function () {
		function drawLineChart(xAxis, series, container, data_detail, chart_name){
			var dataXAxis = xAxis
			var dataSeries = series
			var detail = data_detail

			$('#containerline' + container).highcharts({

				chart: {
					type:'line'
				},
				title: {
					text: chart_name
				},
				tooltip :{
					formatter: function() {
						var index = 0
						var tooltip_str = ""
						var name = this.series.name
						var fetchAt = this.x
						for(var i=0;i<detail.length;i++){
							if(detail[i].name == null || detail[i].name == name){
								for(var j=0;j<detail[i].data.length;j++){
									var detail_data = detail[i].data
									if(fetchAt == detail_data[j].fetchAt){
										for(var k in detail_data[j]){
											if(k != "fetchAt") {
												tooltip_str += "<p>" + k + " : " + detail_data[j][k] + "</p><br>"
											}
										}
									}
								}
							}
						}
						return tooltip_str
					}
				},
				xAxis: dataXAxis ,
				yAxis: {
					title: {
						text: 'Monitoring'
					}
				},
				series: dataSeries
			});
		};

