		// AREA
		$(document ).ready(function(){
			var lstKey = jQuery.parseJSON($("#lstkey").val())
                        var chartName
                        var arrayAll
                        var arrayContainerAll
			if(lstKey.length > 0) {
				for(var eKey=0;eKey<lstKey.length;eKey++){
					var finalKey = lstKey[eKey]
                                        if(($("#jsons>#jsonarea_" + finalKey)).length > 0) {
                                           arrayAll = jQuery.parseJSON($("#jsons>#jsonarea_" + finalKey).val());
                                           arrayContainerAll = jQuery.parseJSON($("#jsons>.lstcontainerarea_" + finalKey).val());
					   var lstContainer = [];
					   var data_all = [];
                                           var chartSize = arrayAll.length
					   for(var i=0;i<chartSize ;i++){
					       data_all = arrayAll[i]
					       lstContainer = arrayContainerAll[i]
					       if(data_all.length >0 && lstContainer.length >0){
						   // data to display on tooltip
						   var data_detail = data_all[0]
						   // draw Line chart
						   for(var j=0;j<data_all.length;j++){
						      if(data_all[j].series[0].name == null){
						         data_all[j].series[0].name = lstContainer[j]
						       }
                                                       chartName = data_all[j].chart_name + " ( " + data_all[j].chart_columns[j] + " )"
						       drawAreaChart(data_all[j].xAxis, data_all[j].series, finalKey + "_" + i + "_" + lstContainer[j], data_all[j].detail_data, chartName)
						   }
					      }
				           }
                                        }
			          }
                        } else {
                                var arrayAll = jQuery.parseJSON($("#jsons>.jsonarea").val());
                                var arrayContainerAll = jQuery.parseJSON($("#jsons>.lstcontainerarea").val());
				var lstContainer = [];
				var data_all = [];
                                var chartSize = arrayAll.length
				for(var i=0;i<chartSize ;i++){
				    data_all = arrayAll[i]
                                    lstContainer = arrayContainerAll[i]
				    if(data_all.length >0 && lstContainer.length >0){
					// data to display on tooltip
					var data_detail = data_all[0]
					// draw area chart
					for(var j=0;j<data_all.length;j++){
                                            if(data_all[j].series[0].name == null){
						data_all[j].series[0].name = lstContainer[j]
					    }
                                            chartName = data_all[j].chart_name + " ( " + data_all[j].chart_columns[j] + " )"
					    drawAreaChart(data_all[j].xAxis, data_all[j].series, i + "_" + lstContainer[j], data_all[j].detail_data, chartName)
					}
                                    }
				}
			}
		});

		// PIE
		$(document ).ready(function(){
                        var lstKey = jQuery.parseJSON($("#lstkey").val())
                        var chartName
                        var arrayAll
                        var arrayContainerAll
			if(lstKey.length > 0) {
				for(var eKey=0;eKey<lstKey.length;eKey++){
					var finalKey = lstKey[eKey]
                                        if(($("#jsons>#jsonpie_" + finalKey)).length > 0) {
                                           arrayAll = jQuery.parseJSON($("#jsons>#jsonpie_" + finalKey).val());
                                           arrayContainerAll = jQuery.parseJSON($("#jsons>.lstcontainerpie_" + finalKey).val());
					   var lstContainer = [];
					   var data_all = [];
                                           var chartSize = arrayAll.length
					  for(var i=0;i<chartSize ;i++){
					       data_all = arrayAll[i]
					       lstContainer = arrayContainerAll[i]
					       if(data_all.length >0 && lstContainer.length >0){
						   // draw Pie chart
						   for(var j=0;j<data_all.length;j++){
                                                       chartName  = data_all[j].chart_name + "( " + data_all[j].name + " )"
						       drawPieChart([data_all[j]], finalKey + "_" + lstContainer[j], chartName)
						   }
					      }
				           }
                                        }
			          }
			} else {
                                var arrayAll = jQuery.parseJSON($("#jsons>.jsonpie").val());
                                var arrayContainerAll = jQuery.parseJSON($("#jsons>.lstcontainerpie").val());
				var lstContainer = [];
				var data_all = [];
                                var chartSize = arrayAll.length
                                var chartNum = 0
				for(var i=0;i<chartSize ;i++){
				    data_all = arrayAll[i]
                                    lstContainer = arrayContainerAll[i]
                                    if(data_all.length >0 && lstContainer.length >0){
                                                   for(var j=0;j<data_all.length;j++){
                                                       if (data_all[j].name != data_all[j].chart_name) {
                                                           chartName  = data_all[j].chart_name + "( " + data_all[j].name + " )"
                                                       } else {
                                                           chartName  = data_all[j].chart_name
                                                       }
						       drawPieChart([data_all[j]], chartNum + "_pie", chartName)
                                                       chartNum++
						   }

                                    }
                                }
			}
		});
	});
	jQuery.noConflict()
});

