//--=============== Start build for livetable
def subTypeURLMap = [:]
def dataModel = []
def dataUrl = ""
result.find{rec->				
    def subtractData = []

    //-- Parsing data and assign data to subtract list
    if(rec.type == "Store"){ //-- Store data type
        subtractData = rec['data']        
        subtractData.each{r2->					
            if(dataModel != null && dataModel.size() <=0){ 
                dataModel.add("Fetch At")
                dataModel.addAll(r2.keySet());					
            } 
        }        
        dataUrl = "/xwiki/bin/get/Lib/LivetableDataAdapter?outputSyntax=plain&job="+job +"&limit="+limit+"&fromDate="+fromDate + "&toDate=" + toDate + "&istIid="+istIid        
        //-- Building data model				
        def model = []
        def colSpec
        dataModel.each{name->
            colSpec = [:]
            colSpec['name'] = name
            colSpec['index'] = name
            colSpec['width'] = "100"
            model.add(colSpec)
        }
        def dataModelStr = (new JsonBuilder(dataModel)).toPrettyString()
        def modelStr = (new JsonBuilder(model)).toPrettyString() 
        str = 
        "{\"url\":\""+dataUrl+"\",\"datatype\":\"json\","+
            "\"colNames\":"+ dataModelStr +
            ",\"colModel\":"+ modelStr +" ,\"rowList\":[10,20,30],"+
            "\"pager\":\"#livetablePager\", \"viewrecords\": true, \"sortname\": \"RECORD_SEQ\","+
            "\"sortorder\": \"asc\", \"caption\":\""+job+"\"}"
        println "{{html clean=\"false\" wiki=\"true\"}}"
        println "<div id='livetableJson' style = \"display:none\">"
        //println "<div id='livetableJson'>"
        println str;
        println "</div>"
        println "{{/html}}"
    }else{ //-- Subtype			
        def subData = rec['data']
        subData.each{key,value->
            dataUrl = "/xwiki/bin/get/Lib/LivetableDataAdapter?outputSyntax=plain&job="+job +"&limit="+limit+"&fromDate="+fromDate + "&toDate=" + toDate + "&istIid="+istIid+"&subType="+key
            subtractData = value
            subtractData.each{r2->					
                if(dataModel != null && dataModel.size() <=0){ 
                    dataModel.add("Fetch At")
                    dataModel.addAll(r2.keySet());					
                } 			
            }
            //-- Building data model				
            def model = []
            def colSpec
            dataModel.each{name->
                colSpec = [:]
                colSpec['name'] = name
                colSpec['index'] = name
                colSpec['width'] = "100"
                model.add(colSpec)
            }
            def dataModelStr = (new JsonBuilder(dataModel)).toPrettyString()
            def modelStr = (new JsonBuilder(model)).toPrettyString() 
            str = 
            "{\"url\":\""+dataUrl+"\",\"datatype\":\"json\","+
                "\"colNames\":"+ dataModelStr +
                ",\"colModel\":"+ modelStr +" ,\"rowList\":[10,20,30],"+
                "\"pager\":\"#livetablePager\", \"viewrecords\": true, \"sortname\": \"RECORD_SEQ\","+
                "\"sortorder\": \"asc\", \"caption\":\""+job+"\"}"
            subTypeURLMap[key] = str
        }
        def finalStr = (new JsonBuilder(subTypeURLMap)).toPrettyString()
        println "{{html clean=\"false\" wiki=\"true\"}}"
        println "<div id='livetableJson' style = \"display:none\">"
        //println "<div id='livetableJson'>"
        println finalStr;
        println "</div>"
        println "{{/html}}"
    }
    return true
}
 //--=============== END

if(listKey.size() <= 0){   
    println "{{html clean=\"false\" wiki=\"true\"}}"
    println "<center>"
    println "<div id=\"livetblContent\" style=\"width:100%;clear:both;align:middle;margin-top:10px\">"   
    println "Statistics Data"
    println "<table id=\"livetableList\" style=\"width:100%;align:center\"></table>"    
    println "<div id=\"livetablePager\"></div>"
    println "</div>"
    println "</center>"

    println "<div id=\"testDiv\">&nbsp;</div>"
    println "{{/html}}"
}
